export const ProfileSideBar = () => {
  const pathname = usePathname();
  const router = useRouter();
  const {logout} = useUser()
  const menuItems = [
    {
      id: "account",
      label: "بيانات الحساب",
      path: "/profile",
      icon: Profile, // ✅ store component
    },
    {
      id: "password",
      label: "تغيير كلمة المرور",
      path: "/change-password",
      icon: LockIcon, // ✅
    },
    {
      id: "courses",
      path:"/my-courses",
      label: "دوراتي",
      icon: OpenBook, // ✅
    },
    {
      id: "badges",
      path:"/my-badges",
      label: "شاراتي",
      icon: Badge,
    },
    {
      id: "certificates",
      path:"/my-certificates",
      label: "شهاداتي",
      icon: Certificate,
    },
    {
      id: "notifications",
      label: "إشعارات",
      path:"/notifications",
      icon: Notification,
    },
    {
      id: "favorites",
      label: "المفضلة",
      path:"/my-favourites",
      icon: Heart,
    },
  ];

  const handleItemClick = (path) => {
    console.log("target_path", path);
    console.log("currentPathname", pathname);
    router.push(path);
  };

  const handleLogout = () => {
    // Handle logout functionality
    console.log("Logout clicked");
  };

  return (
    <nav
      className=" sticky top-0 h-[801px] flex flex-col justify-between bg-white rounded-[30px] overflow-hidden border-[5px] border-solid border-variable-collection-stroke"
      role="navigation"
      aria-label="القائمة الرئيسية"
    >
      <div className="w-[331px] h-[568px] mt-14 flex-col items-start px-4 py-0 flex relative gap-4">
        {menuItems.map((item) => {
          const icon = (
            <item.icon
              className={
                item.path == pathname ? "!fill-primary-bg" : "fill-[#71717A]"
              }
            />
          );

          return (
            <button
              key={item.id}
              onClick={() => handleItemClick(item.path)}
              className={`flex items-center justify-start gap-4 px-4 py-5 relative self-stretch w-full flex-[0_0_auto] rounded-[20px] transition-colors duration-200  focus:outline-none focus:ring-2 focus:ring-primary focus:ring-opacity-50 ${
                item.path == pathname ? "bg-primary" : " text-text"
              }`}
              aria-pressed={item?.path === pathname}
              role="menuitem"
            >
              {icon}
              <div
                className={`relative flex items-center justify-center w-fit mt-[-1.00px]  font-bold text-base text-left tracking-[0] leading-6 whitespace-nowrap ${
                  item.path === pathname ? "text-white" : "text-text"
                }`}
              >
                {item.label}
              </div>
            </button>
          );
        })}
      </div>

      <div className="w-[331px] h-[76px] mb-8 items-center justify-start pt-8 pb-5 px-4 border-t [border-top-style:solid] border-variable-collection-stroke flex relative gap-4">
        <button
          onClick={handleLogout}
          className="flex items-center justify-start gap-4 w-full transition-colors duration-200 hover:bg-red-50 focus:outline-none focus:ring-2 focus:ring-red-500 focus:ring-opacity-50 rounded-lg p-2"
          role="menuitem"
          aria-label="تسجيل الخروج من الحساب"
        >
          <div className="relative w-6 h-6 aspect-[1]">
            <LogoutIcon />
          </div>
          <button onClick={()=> logout()} className="mt-[-1.00px] text-[#e84242] relative flex items-center justify-center w-fit  font-bold text-base text-left tracking-[0] leading-6 whitespace-nowrap ">
            تسجيل الخروج
          </button>
        </button>
      </div>
    </nav>
  );
};

